          {!newsletters?.length && (
            <Card>
              <CardContent className="text-center py-8">
                <Mail className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">No Newsletter Subscriptions</h3>
                <p className="text-muted-foreground">Email subscriptions will appear here when customers sign up.</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import ProductCard from "@/components/product/ProductCard";
import FeaturedProductShowcase from "@/components/product/FeaturedProductShowcase";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from "@/components/ui/select";
import type { Category, Product } from "@shared/schema";

// Helper function to get category-specific properties
function getCategoryProperties(categorySlug: string, product: any) {
  const properties = [];

  switch (categorySlug) {
    case 'clothing':
      properties.push(
        { name: 'Size', value: 'Medium', type: 'size' },
        { name: 'Color', value: 'Navy Blue', type: 'color' },
        { name: 'Material', value: 'Cotton Blend', type: 'material' },
        { name: 'Style', value: 'Casual' }
      );
      break;
    case 'tableware':
      properties.push(
        { name: 'Material', value: 'Ceramic', type: 'material' },
        { name: 'Dimensions', value: '10" x 10"' },
        { name: 'Dishwasher Safe', value: 'Yes' },
        { name: 'Set Size', value: '4 pieces' }
      );
      break;
    case 'kitchen':
      properties.push(
        { name: 'Material', value: 'Stainless Steel', type: 'material' },
        { name: 'Dimensions', value: '12" x 8" x 4"' },
        { name: 'Dishwasher Safe', value: 'Yes' },
        { name: 'Heat Resistant', value: 'Up to 450Â°F' }
      );
      break;
    case 'home-decor':
      properties.push(
        { name: 'Material', value: 'Ceramic & Wood', type: 'material' },
        { name: 'Dimensions', value: '8" x 6" x 10"' },
        { name: 'Color', value: 'Beige', type: 'color' },
        { name: 'Style', value: 'Modern Minimalist' }
      );
      break;
    default:
      // For other categories or new arrivals
      properties.push(
        { name: 'Condition', value: 'New' },
        { name: 'In Store', value: 'Available' }
      );
  }

  return properties;
}

export default function Category({ params }: { params: { slug: string } }) {
  const { slug } = params;
  const [, setLocation] = useLocation();
  const [sortBy, setSortBy] = useState<string>("default");

  const { data: category, isLoading: categoryLoading, error: categoryError } = useQuery({
    queryKey: [`/api/categories/${slug}`],
    queryFn: async () => {
      const response = await fetch(`/api/categories/${slug}`);
      if (!response.ok) throw new Error('Category not found');
      return response.json();
    },
    retry: 1
  });

  const { data: products, isLoading: productsLoading } = useQuery({
    queryKey: [`/api/products/category/${category?.id}`],
    queryFn: async () => {
      const response = await fetch(`/api/products/category/${category?.id}`);
      if (!response.ok) throw new Error('Failed to fetch products');
      return response.json();
    },
    enabled: !!category?.id,
    retry: 1
  });

  useEffect(() => {
    console.log("Category data:", category);

    if (categoryError || (category === null && !categoryLoading)) {
      setLocation("/not-found");
    }
  }, [category, categoryError, categoryLoading, setLocation]);

  const getSortedProducts = () => {
    if (!products) return [];

    const productsCopy = [...products];

    switch (sortBy) {
      case "price-low-high":
        return productsCopy.sort((a, b) => {
          const aPrice = a.discountPrice || a.price;
          const bPrice = b.discountPrice || b.price;
          return aPrice - bPrice;
        });
      case "price-high-low":
        return productsCopy.sort((a, b) => {
          const aPrice = a.discountPrice || a.price;
          const bPrice = b.discountPrice || b.price;
          return bPrice - aPrice;
        });
      case "name-a-z":
        return productsCopy.sort((a, b) => a.name.localeCompare(b.name));
      case "name-z-a":
        return productsCopy.sort((a, b) => b.name.localeCompare(a.name));
      case "discount":
        return productsCopy.sort((a, b) => {
          const aDiscount = a.discountPrice ? (a.price - a.discountPrice) / a.price : 0;
          const bDiscount = b.discountPrice ? (b.price - b.discountPrice) / b.price : 0;
          return bDiscount - aDiscount;
        });
      default:
        return productsCopy;
    }
  };

  const sortedProducts = getSortedProducts();
  console.log("Sorted products:", sortedProducts);

  return (
    <div className="container mx-auto px-4 py-8">
      {categoryLoading ? (
        <div className="mb-6">
          <Skeleton className="h-10 w-1/4 mb-2" />
          <Skeleton className="h-4 w-2/3" />
        </div>
      ) : (
        <div className="mb-6">
          <h1 className="text-3xl font-bold mb-2">{category?.name}</h1>
          <p className="text-gray-600">Browse our collection of {category?.name.toLowerCase()}</p>
        </div>
      )}

      <div className="flex justify-end mb-6">
        <Select value={sortBy} onValueChange={setSortBy}>
          <SelectTrigger className="w-[180px]">
            <SelectValue placeholder="Sort by" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="default">Featured</SelectItem>
            <SelectItem value="price-low-high">Price: Low to High</SelectItem>
            <SelectItem value="price-high-low">Price: High to Low</SelectItem>
            <SelectItem value="name-a-z">Name: A to Z</SelectItem>
            <SelectItem value="name-z-a">Name: Z to A</SelectItem>
            <SelectItem value="discount">Biggest Discount</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {productsLoading ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array(8).fill(0).map((_, i) => (
            <div key={i} className="aspect-[4/3]">
              <Skeleton className="w-full h-full rounded-lg" />
              <div className="p-4 space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-4 w-1/4" />
                <div className="flex justify-between">
                  <Skeleton className="h-4 w-1/4" />
                  <Skeleton className="h-8 w-1/4" />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : sortedProducts.length > 0 ? (
        <>
          {/* Featured Product Showcase */}
          {sortedProducts.length > 0 && slug && (
            <FeaturedProductShowcase 
              product={{
                ...sortedProducts[0],
                description: sortedProducts[0].description || "Experience quality and style with this premium item from our collection."
              }}
              properties={getCategoryProperties(slug, sortedProducts[0])}
            />
          )}

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {sortedProducts.map((product) => (
              <ProductCard
                key={product.id}
                id={product.id}
                slug={product.slug}
                name={product.name}
                imageUrl={product.imageUrl}
                price={product.price}
                discountPrice={product.discountPrice}
                stockLevel={product.stockLevel}
                isNew={product.isNew}
              />
            ))}
          </div>
        </>
      ) : (
        <div className="text-center py-12">
          <h3 className="text-xl font-medium mb-2">No products found</h3>
          <p className="text-gray-600">
            We couldn't find any products in this category.
          </p>
        </div>
      )}
    </div>
  );
}

import { useState } from "react";
import { useLocation } from "wouter";
import { useCart } from "@/context/CartContext";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";
import { Trash2, Minus, Plus } from "lucide-react";
import { convertToRwandanFrancs, formatRwandanFrancs } from "@/lib/currency";

const checkoutFormSchema = z.object({
  customerName: z.string().min(2, { message: "Name must be at least 2 characters" }),
  customerEmail: z.string().email({ message: "Please enter a valid email address" }),
  customerPhone: z.string().min(10, { message: "Please enter a valid phone number" }),
});

type CheckoutFormValues = z.infer<typeof checkoutFormSchema>;

export default function Checkout() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const { 
    cartItems, 
    removeItem, 
    updateQuantity, 
    getCartTotal, 
    getTaxAmount, 
    getFinalTotal, 
    clearCart,
    isLoading 
  } = useCart();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<CheckoutFormValues>({
    resolver: zodResolver(checkoutFormSchema),
    defaultValues: {
      customerName: "",
      customerEmail: "",
      customerPhone: "",
    },
  });

  const onSubmit = async (data: CheckoutFormValues) => {
    if (cartItems.length === 0) {
      toast({
        title: "Cart is empty",
        description: "Add some items to your cart before checking out",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);
    try {
      // Prepare order data
      const orderData = {
        order: {
          ...data,
          totalAmount: getFinalTotal(),
          status: "pending",
        },
        items: cartItems.map(item => ({
          productId: item.productId,
          quantity: item.quantity,
          price: (item.product.discountPrice || item.product.price) * (item.product.setPieces || 1),
        })),
      };

      // Create order
      const response = await apiRequest("POST", "/api/orders", orderData);
      const order = await response.json();

      // Clear the cart
      await clearCart();

      // Navigate to order confirmation
      navigate(`/order-confirmation/${order.id}`);

      toast({
        title: "Order placed successfully",
        description: "Thank you for your order!",
      });
    } catch (error) {
      toast({
        title: "Error placing order",
        description: "There was a problem placing your order. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleIncreaseQuantity = (itemId: number, currentQuantity: number) => {
    updateQuantity(itemId, currentQuantity + 1);
  };

  const handleDecreaseQuantity = (itemId: number, currentQuantity: number) => {
    if (currentQuantity > 1) {
      updateQuantity(itemId, currentQuantity - 1);
    } else {
      removeItem(itemId);
    }
  };

  if (cartItems.length === 0 && !isLoading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="max-w-md mx-auto text-center">
          <h1 className="text-2xl font-bold mb-4">Your Cart is Empty</h1>
          <p className="text-gray-600 mb-6">
            Add some items to your cart before proceeding to checkout.
          </p>
          <Button onClick={() => navigate("/")}>Continue Shopping</Button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Checkout</h1>

      <div className="flex flex-col lg:flex-row gap-8">
        {/* Customer Information */}
        <div className="lg:w-1/2">
          <Card>
            <CardHeader>
              <CardTitle>Customer Information</CardTitle>
              <CardDescription>
                Please enter your details for order pickup
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <FormField
                    control={form.control}
                    name="customerName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Full Name</FormLabel>
                        <FormControl>
                          <Input placeholder="John Doe" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerEmail"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email Address</FormLabel>
                        <FormControl>
                          <Input placeholder="john.doe@example.com" type="email" {...field} />
                        </FormControl>
                        <FormDescription>
                          We'll send your order confirmation to this email
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerPhone"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Phone Number</FormLabel>
                        <FormControl>
                          <Input placeholder="(555) 123-4567" {...field} />
                        </FormControl>
                        <FormDescription>
                          We'll contact you when your order is ready for pickup
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="bg-gray-50 p-4 rounded-lg mb-4">
                    <h3 className="font-medium mb-2">Pickup Information</h3>
                    <p className="text-sm text-gray-600 mb-2">
                      Your order will be available for pickup at our store location:
                    </p>
                    <p className="text-sm">
                      <strong>DiscountMart</strong><br />
                      123 Main Street<br />
                      Anytown, ST 12345
                    </p>
                  </div>

                  <Button
                    type="submit"
                    className="w-full"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? "Processing..." : "Place Order"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        </div>

        {/* Order Summary */}
        <div className="lg:w-1/2">
          <Card>
            <CardHeader>
              <CardTitle>Order Summary</CardTitle>
              <CardDescription>
                Review your items before placing your order
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {isLoading ? (
                <p>Loading cart items...</p>
              ) : (
                <>
                  {cartItems.map((item) => (
                    <div key={item.id} className="flex items-center py-4 border-b border-gray-200 last:border-b-0">
                      <img
                        src={item.product.imageUrl}
                        alt={item.product.name}
                        className="w-16 h-16 object-cover rounded"
                      />
                      <div className="ml-4 flex-grow">
                        <h3 className="font-medium">{item.product.name}</h3>
                        {item.product.setPieces > 1 && (
                          <p className="text-xs text-gray-600">
                            {item.product.setPieces} {item.product.unitType === "set" ? "pieces per set" : `${item.product.unitType}s included`}
                          </p>
                        )}
                        <p className="text-blue-800 font-bold">
                          {formatRwandanFrancs(convertToRwandanFrancs(item.product.discountPrice || item.product.price))}
                          {item.product.setPieces > 1 && (
                            <span className="text-sm text-gray-600 ml-1">per {item.product.unitType}</span>
                          )}
                        </p>
                        <div className="flex items-center mt-2">
                          <Button
                            variant="outline"
                            size="icon"
                            className="h-6 w-6 rounded-full"
                            onClick={() => handleDecreaseQuantity(item.id, item.quantity)}
                          >
                            <Minus className="h-3 w-3" />
                          </Button>
                          <span className="mx-2">{item.quantity}</span>
                          <Button
                            variant="outline"
                            size="icon"
                            className="h-6 w-6 rounded-full"
                            onClick={() => handleIncreaseQuantity(item.id, item.quantity)}
                          >
                            <Plus className="h-3 w-3" />
                          </Button>
                        </div>
                      </div>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => removeItem(item.id)}
                        className="text-gray-400 hover:text-red-500"
                      >
                        <Trash2 className="h-5 w-5" />
                      </Button>
                    </div>
                  ))}
                </>
              )}

              <Separator className="my-4" />

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span className="font-medium text-blue-800">{formatRwandanFrancs(convertToRwandanFrancs(getCartTotal()))}</span>
                </div>
                <div className="flex justify-between">
                  <span>Tax (18%):</span>
                  <span className="font-medium text-blue-800">{formatRwandanFrancs(convertToRwandanFrancs(getTaxAmount()))}</span>
                </div>
                <Separator className="my-2" />
                <div className="flex justify-between text-lg">
                  <span className="font-bold">Total:</span>
                  <span className="font-bold text-blue-800">{formatRwandanFrancs(convertToRwandanFrancs(getFinalTotal()))}</span>
                </div>
              </div>
            </CardContent>
            <CardFooter className="bg-gray-50 rounded-b-lg">
              <div className="w-full">
                <p className="text-sm text-gray-600 mb-4">
                  By placing your order, you agree to our terms and conditions. 
                  Your items will be available for pickup at our store location.
                </p>
              </div>
            </CardFooter>
          </Card>
        </div>
      </div>
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import ProductCard from "@/components/product/ProductCard";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import type { Product } from "@shared/schema";

export default function Clearance() {
  const { data: products, isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    queryFn: async () => {
      const response = await fetch("/api/products");
      if (!response.ok) throw new Error('Failed to fetch products');
      return response.json();
    },
  });

  // Filter products that have discounts (clearance items)
  const clearanceProducts = products?.filter(product => product.discountPrice !== null) || [];

  // Calculate average discount percentage for display
  const averageDiscount = clearanceProducts.length > 0 
    ? Math.round(clearanceProducts.reduce((sum, product) => {
        const discount = product.discountPrice 
          ? ((product.price - product.discountPrice) / product.price) * 100 
          : 0;
        return sum + discount;
      }, 0) / clearanceProducts.length)
    : 0;

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header Section */}
      <div className="text-center mb-12">
        <div className="flex justify-center items-center gap-3 mb-4">
          <Badge className="bg-red-500 text-white text-lg px-4 py-2">
            CLEARANCE SALE
          </Badge>
          <Badge variant="outline" className="text-red-600 border-red-600 text-lg px-4 py-2">
            Up to {averageDiscount}% OFF
          </Badge>
        </div>
        <h1 className="text-4xl font-bold mb-4 text-gray-900">
          Clearance Items
        </h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Discover amazing deals on quality products! These discounted items are available for a limited time. 
          Perfect for budget-conscious shoppers looking for great value.
        </p>
      </div>

      {/* Products Grid */}
      {isLoading ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array(8).fill(0).map((_, i) => (
            <div key={i} className="aspect-[4/3]">
              <Skeleton className="w-full h-full rounded-lg" />
              <div className="p-4 space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-4 w-1/4" />
                <div className="flex justify-between">
                  <Skeleton className="h-4 w-1/4" />
                  <Skeleton className="h-8 w-1/4" />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : clearanceProducts.length > 0 ? (
        <>
          <div className="mb-6 text-center">
            <p className="text-gray-600">
              <span className="font-semibold text-red-600">{clearanceProducts.length}</span> clearance items available
            </p>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {clearanceProducts.map((product) => (
              <ProductCard
                key={product.id}
                id={product.id}
                slug={product.slug}
                name={product.name}
                imageUrl={product.imageUrl}
                price={product.price}
                discountPrice={product.discountPrice}
                stockLevel={product.stockLevel}
                isNew={product.isNew}
              />
            ))}
          </div>
        </>
