import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import ProductCard from "@/components/product/ProductCard";
import FeaturedProductShowcase from "@/components/product/FeaturedProductShowcase";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from "@/components/ui/select";
import type { Product } from "@shared/schema";

export default function NewArrivals() {
  const [sortBy, setSortBy] = useState<string>("default");

  const { data: products, isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/new"],
    queryFn: async () => {
      const response = await fetch("/api/products/new");
      if (!response.ok) throw new Error('Failed to fetch new products');
      return response.json();
    },
  });

  const getSortedProducts = () => {
    if (!products) return [];

    const productsCopy = [...products];

    switch (sortBy) {
      case "price-low-high":
        return productsCopy.sort((a, b) => {
          const aPrice = a.discountPrice || a.price;
          const bPrice = b.discountPrice || b.price;
          return aPrice - bPrice;
        });
      case "price-high-low":
        return productsCopy.sort((a, b) => {
          const aPrice = a.discountPrice || a.price;
          const bPrice = b.discountPrice || b.price;
          return bPrice - aPrice;
        });
      case "name-a-z":
        return productsCopy.sort((a, b) => a.name.localeCompare(b.name));
      case "name-z-a":
        return productsCopy.sort((a, b) => b.name.localeCompare(a.name));
      case "discount":
        return productsCopy.sort((a, b) => {
          const aDiscount = a.discountPrice ? (a.price - a.discountPrice) / a.price : 0;
          const bDiscount = b.discountPrice ? (b.price - b.discountPrice) / b.price : 0;
          return bDiscount - aDiscount;
        });
      default:
        return productsCopy;
    }
  };

  const sortedProducts = getSortedProducts();

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">New Arrivals</h1>
        <p className="text-gray-600">Check out our newest products just added to our store</p>
      </div>

      <div className="flex justify-end mb-6">
        <Select value={sortBy} onValueChange={setSortBy}>
          <SelectTrigger className="w-[180px]">
            <SelectValue placeholder="Sort by" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="default">Featured</SelectItem>
            <SelectItem value="price-low-high">Price: Low to High</SelectItem>
            <SelectItem value="price-high-low">Price: High to Low</SelectItem>
            <SelectItem value="name-a-z">Name: A to Z</SelectItem>
            <SelectItem value="name-z-a">Name: Z to A</SelectItem>
            <SelectItem value="discount">Biggest Discount</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {productsLoading ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array(8).fill(0).map((_, i) => (
            <div key={i} className="aspect-[4/3]">
              <Skeleton className="w-full h-full rounded-lg" />
              <div className="p-4 space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-4 w-1/4" />
                <div className="flex justify-between">
                  <Skeleton className="h-4 w-1/4" />
                  <Skeleton className="h-8 w-1/4" />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : sortedProducts.length > 0 ? (
        <>
          {/* Featured New Arrival Showcase */}
          {sortedProducts.length > 0 && (
            <FeaturedProductShowcase 
              product={{
                ...sortedProducts[0],
                description: sortedProducts[0].description || "Be among the first to experience this brand new addition to our collection. Just arrived and already turning heads!"
              }}
              properties={[
                { name: 'Release Date', value: 'New This Week' },
                { name: 'Available In Store', value: 'Yes' },
                { name: 'Collection', value: 'Spring 2025' },
                { name: 'Limited Edition', value: 'Yes' }
              ]}
            />
          )}

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {sortedProducts.map((product) => (
              <ProductCard
                key={product.id}
                id={product.id}
                slug={product.slug}
                name={product.name}
                imageUrl={product.imageUrl}
                price={product.price}
                discountPrice={product.discountPrice}
                stockLevel={product.stockLevel}
                isNew={true}
              />
            ))}
          </div>
        </>
      ) : (
        <div className="text-center py-12">
          <h3 className="text-xl font-medium mb-2">No new products found</h3>
          <p className="text-gray-600">
            Check back soon for our newest arrivals!
          </p>
        </div>
      )}
    </div>
  );
}

import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Search, Package, Clock, CheckCircle, AlertCircle } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { formatRwandanFrancs, convertToRwandanFrancs } from "@/lib/currency";
import type { Order, OrderItem, Product } from "@shared/schema";

export default function OrderStatus() {
  const [orderId, setOrderId] = useState("");
  const [searchOrderId, setSearchOrderId] = useState("");
  const [location] = useLocation();

  // Check for order ID in URL parameters
  useEffect(() => {
    const params = new URLSearchParams(location.split('?')[1] || '');
    const urlOrderId = params.get('id');
    if (urlOrderId) {
      setOrderId(urlOrderId);
      setSearchOrderId(urlOrderId);
    }
  }, [location]);

  const { data: order, isLoading, error } = useQuery<Order & { items: (OrderItem & { product: Product })[] }>({
    queryKey: [`/api/orders/${searchOrderId}`],
    queryFn: async () => {
      const response = await fetch(`/api/orders/${searchOrderId}`);
      if (!response.ok) throw new Error('Order not found');
      return response.json();
    },
    enabled: !!searchOrderId && searchOrderId.length > 0,
    retry: false
  });

  const handleSearch = () => {
    if (orderId.trim()) {
      setSearchOrderId(orderId.trim());
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'pending':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'processing':
        return <Package className="h-5 w-5 text-blue-500" />;
      case 'ready':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'completed':
        return <CheckCircle className="h-5 w-5 text-green-600" />;
      default:
        return <AlertCircle className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      case 'ready':
        return 'bg-green-100 text-green-800';
      case 'completed':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Order Status</h1>
          <p className="text-gray-600">Track your order and get updates on your pickup status</p>
        </div>

        {/* Search Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Search className="h-5 w-5" />
              <span>Find Your Order</span>
            </CardTitle>
            <CardDescription>
              Enter your order ID to track your order status and pickup information
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex space-x-4">
              <Input
                placeholder="Enter Order ID (e.g., 12345)"
                value={orderId}
                onChange={(e) => setOrderId(e.target.value)}
                className="flex-1"
              />
              <Button onClick={handleSearch} disabled={!orderId.trim()}>
                Search Order
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Results Section */}
        {isLoading && (
          <Card>
            <CardHeader>
              <Skeleton className="h-6 w-48" />
              <Skeleton className="h-4 w-32" />
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <Skeleton className="h-4 w-full" />
                <Skeleton className="h-4 w-3/4" />
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <Skeleton className="h-32" />
                  <Skeleton className="h-32" />
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {error && searchOrderId && (
          <Card>
            <CardContent className="pt-6">
              <div className="text-center py-8">
                <AlertCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Order Not Found</h3>
                <p className="text-gray-600 mb-4">
                  We couldn't find an order with ID "{searchOrderId}". Please check your order ID and try again.
                </p>
                <p className="text-sm text-gray-500">
                  Order IDs are typically sent to your email after placing an order.
                </p>
              </div>
            </CardContent>
          </Card>
        )}

        {order && (
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle className="flex items-center space-x-2">
                    {getStatusIcon(order.status)}
                    <span>Order #{order.id}</span>
                  </CardTitle>
                  <CardDescription>
                    Placed on {order.createdAt ? new Date(order.createdAt.toString()).toLocaleDateString('en-US', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    }) : 'Unknown date'}
                  </CardDescription>
                </div>
                <Badge className={getStatusColor(order.status)}>
                  {order.status?.charAt(0).toUpperCase() + order.status?.slice(1)}
                </Badge>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Customer Information</h4>
                  <p className="text-gray-600">{order.customerName}</p>
                  <p className="text-gray-600">{order.customerEmail}</p>
                  <p className="text-gray-600">{order.customerPhone}</p>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Pickup Information</h4>
                  <p className="text-gray-600">DiscountMart Store</p>
                  <p className="text-gray-600">Downtown Kigali, Rwanda</p>
                  <p className="text-gray-600">
                    Status: {order.status === 'ready' ? 'Ready for pickup' : 'Being prepared'}
                  </p>
                </div>
              </div>

              {order.items && order.items.length > 0 && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-4">Order Items</h4>
                  <div className="space-y-3 mb-4">
                    {order.items.map((item) => (
                      <div key={item.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center space-x-3">
                          <img
                            src={item.product.imageUrl}
                            alt={item.product.name}
                            className="w-12 h-12 object-cover rounded"
                          />
                          <div>
                            <p className="font-medium text-gray-900">{item.product.name}</p>
                            <p className="text-sm text-gray-600">Quantity: {item.quantity}</p>
                          </div>
                        </div>
                        <p className="font-semibold text-gray-900">
                          {formatRwandanFrancs(convertToRwandanFrancs(item.price))}
                        </p>
                      </div>
                    ))}
                  </div>
                  <div className="border-t pt-4">
                    <div className="flex justify-between items-center text-lg font-semibold text-gray-900">
                      <span>Total</span>
                      <span>{formatRwandanFrancs(convertToRwandanFrancs(order.totalAmount))}</span>
                    </div>
                  </div>
                </div>
              )}

              {order.status === 'ready' && (
                <div className="mt-6 p-4 bg-green-50 rounded-lg border border-green-200">
                  <div className="flex items-center space-x-2">
                    <CheckCircle className="h-5 w-5 text-green-600" />
                    <p className="font-semibold text-green-800">Your order is ready for pickup!</p>
                  </div>
                  <p className="text-green-700 mt-1">
                    Please visit our store in downtown Kigali to collect your items.
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {!order && !isLoading && !error && searchOrderId && (
          <Card>
            <CardContent className="pt-6">
              <div className="text-center py-8">
                <Package className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">No Order Found</h3>
                <p className="text-gray-600">
                  Enter your order ID above to track your order status.
                </p>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useCart } from "@/context/CartContext";
import { useRecentlyViewed } from "@/context/RecentlyViewedContext";
import { useWishlist } from "@/context/WishlistContext";
import RecentlyViewedProducts from "@/components/product/RecentlyViewedProducts";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Separator } from "@/components/ui/separator";
import { CheckCircle, AlertCircle, Minus, Plus, Heart } from "lucide-react";
import { convertToRwandanFrancs, formatRwandanFrancs } from "@/lib/currency";
import type { Product } from "@shared/schema";

export default function ProductDetail({ params }: { params: { slug: string } }) {
  const { slug } = params;
  const [, setLocation] = useLocation();
  const [quantity, setQuantity] = useState(1);
  const { addToCart, isLoading: isCartLoading } = useCart();
  const { addToRecentlyViewed } = useRecentlyViewed();
  const { isInWishlist, toggleWishlist } = useWishlist();

  const { data: product, isLoading } = useQuery<Product>({
    queryKey: [`/api/products/${slug}`],
    queryFn: async () => {
      const res = await fetch(`/api/products/${slug}`);
      if (!res.ok) throw new Error('Product not found');
      return res.json();
    }
  });

  const { data: relatedProducts, isLoading: relatedLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/category", product?.categoryId],
    enabled: !!product?.categoryId,
    queryFn: async () => {
      const res = await fetch(`/api/products/category/${product?.categoryId}`);
      if (!res.ok) throw new Error('Related products not found');
      return res.json();
    }
  });

  // Add product to recently viewed when it loads
  useEffect(() => {
    if (product && product.id) {
      addToRecentlyViewed({
        id: product.id,
        slug: product.slug,
        name: product.name,
        imageUrl: product.imageUrl,
        price: product.price,
        discountPrice: product.discountPrice,
        stockLevel: product.stockLevel,
        categoryId: product.categoryId
      });
    }
  }, [product, addToRecentlyViewed]);

  useEffect(() => {
    if (product === null) {
      setLocation("/not-found");
    }
  }, [product, setLocation]);

  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id, quantity);
    }
  };

  const handleToggleWishlist = () => {
    if (product) {
      toggleWishlist({
        id: product.id,
        slug: product.slug,
        name: product.name,
        imageUrl: product.imageUrl,
        price: product.price,
        discountPrice: product.discountPrice,
      });
    }
  };

  const incrementQuantity = () => setQuantity(q => q + 1);
  const decrementQuantity = () => setQuantity(q => Math.max(1, q - 1));

  const isInStock = product?.stockLevel === "In Stock";
  const isLowStock = product?.stockLevel === "Low Stock";
  const discountPercentage = product?.discountPrice 
    ? Math.round(((product.price - product.discountPrice) / product.price) * 100) 
    : 0;
  const inWishlist = product ? isInWishlist(product.id) : false;

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col md:flex-row gap-8">
          <div className="md:w-1/2">
            <Skeleton className="w-full aspect-square rounded-lg" />
          </div>
          <div className="md:w-1/2">
            <Skeleton className="h-10 w-3/4 mb-2" />
            <Skeleton className="h-6 w-1/4 mb-4" />
            <Skeleton className="h-4 w-full mb-2" />
            <Skeleton className="h-4 w-full mb-2" />
            <Skeleton className="h-4 w-3/4 mb-6" />
            <Skeleton className="h-10 w-full" />
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return null;
  }

  const filteredRelatedProducts = relatedProducts?.filter(p => p.id !== product.id).slice(0, 4) || [];

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col md:flex-row gap-8">
        {/* Product Image */}
        <div className="md:w-1/2">
          <div className="bg-white rounded-lg overflow-hidden">
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full object-cover"
            />
          </div>
        </div>

        {/* Product Info */}
        <div className="md:w-1/2">
          <h1 className="text-3xl font-bold mb-2">{product.name}</h1>

          <div className="flex items-center mb-4">
            {product.discountPrice ? (
              <>
                <span className="text-2xl text-blue-800 font-bold mr-3">
                  {formatRwandanFrancs(convertToRwandanFrancs(product.discountPrice))}
                </span>
                <span className="text-gray-500 text-lg line-through">
                  {formatRwandanFrancs(convertToRwandanFrancs(product.price))}
                </span>
                <span className="ml-2 bg-secondary text-white text-sm font-bold px-2 py-1 rounded">
                  {discountPercentage}% OFF
                </span>
              </>
            ) : (
              <span className="text-2xl text-blue-800 font-bold">
                {formatRwandanFrancs(convertToRwandanFrancs(product.price))}
              </span>
            )}
          </div>

          <div className="mb-4">
            {isInStock ? (
              <span className="text-success flex items-center">
                <CheckCircle className="h-5 w-5 mr-1" /> In Stock
              </span>
            ) : isLowStock ? (
              <span className="text-warning flex items-center">
                <AlertCircle className="h-5 w-5 mr-1" /> Low Stock
              </span>
            ) : (
              <span className="text-error flex items-center">
                <AlertCircle className="h-5 w-5 mr-1" /> Out of Stock
              </span>
            )}
          </div>

          <p className="text-gray-700 mb-6">
            {product.description}
          </p>

          <div className="mb-6">
            <label className="block text-sm font-medium mb-2">Quantity</label>
            <div className="flex items-center">
              <Button
                variant="outline"
                size="icon"
                className="rounded-r-none"
                onClick={decrementQuantity}
                disabled={quantity <= 1}
              >
                <Minus className="h-4 w-4" />
              </Button>
              <div className="px-4 py-2 border-t border-b border-gray-300 text-center w-12">
                {quantity}
              </div>
              <Button
                variant="outline"
                size="icon"
                className="rounded-l-none"
                onClick={incrementQuantity}
              >
                <Plus className="h-4 w-4" />
              </Button>
            </div>
          </div>

          <div className="flex gap-2 mb-6">
            <Button
              className="flex-1"
              size="lg"
              onClick={handleAddToCart}
              disabled={isCartLoading || !isInStock}
            >
              Add to Cart
            </Button>
            <Button 
              variant={inWishlist ? "default" : "outline"} 
              size="icon" 
              className={inWishlist ? "text-white bg-secondary hover:bg-secondary/90" : "text-gray-500 hover:text-secondary"}
              onClick={handleToggleWishlist}
            >
              <Heart className={`h-5 w-5 ${inWishlist ? "fill-current" : ""}`} />
            </Button>
          </div>

          <Card className="mt-6">
            <CardContent className="pt-6">
              <h3 className="font-bold mb-4">Store Pickup Details</h3>
              <p className="text-sm text-gray-700 mb-2">
                This item is available for in-store pickup. Once your order is placed, we'll prepare it for pickup at our store.
              </p>
              <p className="text-sm text-gray-700">
                You'll receive an email notification when your order is ready for pickup, typically within 24 hours.
              </p>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Related Products */}
      {filteredRelatedProducts.length > 0 && (
        <div className="mt-12">
          <Separator className="mb-6" />
          <h2 className="text-2xl font-bold mb-6">You May Also Like</h2>

          {relatedLoading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {Array(4).fill(0).map((_, i) => (
                <div key={i} className="aspect-[4/3]">
                  <Skeleton className="w-full h-full rounded-lg" />
                  <div className="p-4 space-y-2">
                    <Skeleton className="h-4 w-3/4" />
                    <Skeleton className="h-4 w-1/4" />
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {filteredRelatedProducts.map((related) => (
                <div key={related.id} className="bg-white rounded-lg shadow-sm overflow-hidden hover:shadow-md transition">
                  <a href={`/product/${related.slug}`} className="block">
                    <div className="aspect-[4/3]">
                      <img 
                        src={related.imageUrl} 
                        alt={related.name} 
                        className="w-full h-full object-cover"
                      />
                    </div>
                    <div className="p-4">
                      <h3 className="font-medium mb-1">{related.name}</h3>
                      <div className="flex items-center">
                        {related.discountPrice ? (
                          <>
                            <span className="text-blue-800 font-bold mr-2">
                              {formatRwandanFrancs(convertToRwandanFrancs(related.discountPrice))}
                            </span>
                            <span className="text-gray-500 text-sm line-through">
                              {formatRwandanFrancs(convertToRwandanFrancs(related.price))}
                            </span>
                          </>
                        ) : (
                          <span className="text-blue-800 font-bold">
                            {formatRwandanFrancs(convertToRwandanFrancs(related.price))}
                          </span>
                        )}
                      </div>
                    </div>
                  </a>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Recently Viewed Products */}
      <RecentlyViewedProducts />
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import ProductCard from "@/components/product/ProductCard";
import { Skeleton } from "@/components/ui/skeleton";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Search as SearchIcon } from "lucide-react";
import type { Product } from "@shared/schema";

export default function Search() {
  const [location, navigate] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [activeQuery, setActiveQuery] = useState("");

  // Extract search query from URL parameters
  useEffect(() => {
    const params = new URLSearchParams(location.split('?')[1] || '');
    const query = params.get('q') || '';
    setSearchQuery(query);
    setActiveQuery(query);
  }, [location]);

  const { data: searchResults, isLoading } = useQuery<Product[]>({
    queryKey: [`/api/products/search`, activeQuery],
    queryFn: async () => {
      if (!activeQuery.trim()) return [];
      const response = await fetch(`/api/products/search?q=${encodeURIComponent(activeQuery)}`);
      if (!response.ok) throw new Error('Failed to search products');
      return response.json();
    },
    enabled: !!activeQuery.trim(),
    retry: 1
  });

  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Search Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-4">Search Products</h1>

        {/* Search Form */}
        <form onSubmit={handleSearchSubmit} className="flex gap-4 max-w-md">
          <Input
            type="text"
            placeholder="Search for products..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="flex-1"
          />
          <Button type="submit" disabled={!searchQuery.trim()}>
            <SearchIcon className="h-4 w-4 mr-2" />
            Search
          </Button>
        </form>
      </div>

      {/* Search Results */}
      {activeQuery && (
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">
            Search Results for "{activeQuery}"
          </h2>
          {!isLoading && searchResults && (
            <p className="text-gray-600">
              Found {searchResults.length} {searchResults.length === 1 ? 'product' : 'products'}
            </p>
          )}
        </div>
      )}

      {/* Loading State */}
      {isLoading && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {Array(8).fill(0).map((_, i) => (
            <div key={i} className="space-y-4">
              <Skeleton className="aspect-square rounded-lg" />
              <div className="space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-4 w-1/2" />
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Search Results Grid */}
      {!isLoading && searchResults && searchResults.length > 0 && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {searchResults.map((product) => (
            <ProductCard
              key={product.id}
              id={product.id}
              slug={product.slug}
              name={product.name}
              imageUrl={product.imageUrl}
              price={product.price}
              discountPrice={product.discountPrice}
              stockLevel={product.stockLevel}
              isNew={product.isNew}
              setPieces={product.setPieces || 1}
              unitType={product.unitType || "piece"}
            />
          ))}
        </div>
      )}

      {/* No Results */}
      {!isLoading && activeQuery && searchResults && searchResults.length === 0 && (
        <div className="text-center py-12">
          <SearchIcon className="h-16 w-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            No products found
          </h3>
          <p className="text-gray-600 mb-6">
            We couldn't find any products matching "{activeQuery}". Try adjusting your search terms.
          </p>
          <Button onClick={() => navigate("/")}>
            Browse All Products
          </Button>
        </div>
      )}

      {/* Initial State - No Search */}
      {!activeQuery && (
        <div className="text-center py-12">
          <SearchIcon className="h-16 w-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            Start Your Search
          </h3>
          <p className="text-gray-600">
            Enter keywords to find your favorite products
          </p>
        </div>
      )}
    </div>
  );
}

